<?php

namespace MarketplaceBundle\Repository;

/**
 * ItemsRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class ItemsRepository extends \Doctrine\ORM\EntityRepository
{
	public function gelAllItems()
	{
		$query = $this
			->getEntityManager()
			->createQuery(
				'SELECT i.id, i.name, i.stock, p.url, i.priceHt 
				FROM MarketplaceBundle:Items i, MarketplaceBundle:Picture p
				WHERE i.id = p.items
				ORDER BY p.items
				');
		return $query->getResult();

	}

	public function findArray($array)
	{
		$query = $this
			->getEntityManager()
			->createQuery(
				'
				SELECT i.id, i.name, i.stock, i.priceHt, p.url
				FROM MarketplaceBundle:Items i, MarketplaceBundle:Picture p
				WHERE i.id = p.items
				AND i.id IN (:array)
				ORDER BY p.items
				'
				)
			->setParameter("array",$array);
		return $query-> getResult();
	}
}
